import java.util.Scanner;public class PithyPasstimes{	public static void main (String [] args)	{		Scanner s = new Scanner (System.in);		int n = s.nextInt();		int count = 0;		for(int i = 0; i < n; i++)		{			String word = s.next();			if(word.length() <= 10)				count++;		}				System.out.println(count);	}}